package com.oculus.signature;

import android.content.Context;
import android.content.pm.PackageManager;
import android.content.pm.Signature;
import android.util.Log;

public class SignatureHelper {
    public static final Signature FBANDROID_FIRST_PARTY_DEBUG_SIG = new Signature(FBANDROID_SIGNATURE_HASH_DEBUG);
    public static final Signature FBANDROID_FIRST_PARTY_RELEASE_SIG = new Signature(FBANDROID_SIGNATURE_HASH_RELEASE);
    public static final String FBANDROID_SIGNATURE_HASH_DEBUG = "";
    public static final String FBANDROID_SIGNATURE_HASH_RELEASE = "";
    public static final Signature GO_PLATFORM_RELEASE_SIGNATURE = new Signature(GO_PLATFORM_SIGNATURE_HASH_RELEASE);
    public static final String GO_PLATFORM_SIGNATURE_HASH_RELEASE = "";
    public static final Signature MIRAMAR_PLATFORM_RELEASE_SIGNATURE = new Signature(MIRAMAR_PLATFORM_SIGNATURE_HASH_RELEASE);
    public static final String MIRAMAR_PLATFORM_SIGNATURE_HASH_RELEASE = "";
    public static final String OCULUS_CORE_RELEASE_CERTIFICATE = "";
    public static final Signature OCULUS_CORE_RELEASE_SIGNATURE = new Signature(OCULUS_CORE_RELEASE_CERTIFICATE);
    private static final String OCULUS_FIRST_PARTY_DEBUG_CERT = "";
    public static final Signature OCULUS_FIRST_PARTY_DEBUG_SIG = new Signature(OCULUS_FIRST_PARTY_DEBUG_CERT);
    private static final String OCULUS_FIRST_PARTY_PROD_CERT = "";
    public static final Signature OCULUS_FIRST_PARTY_PROD_SIG = new Signature(OCULUS_FIRST_PARTY_PROD_CERT);
    public static final String OCULUS_PRIV_APPS_RELEASE = "";
    public static final Signature OCULUS_PRIV_APPS_RELEASE_SIG = new Signature(OCULUS_PRIV_APPS_RELEASE);
    public static final String OCULUS_PROD_RELEASE_CERTIFICATE = "";
    public static final Signature OCULUS_PROD_RELEASE_SIGNATURE = new Signature(OCULUS_PROD_RELEASE_CERTIFICATE);
    private static final String OCULUS_TV_PROD_CERT = "";
    public static final Signature OCULUS_TV_PROD_SIG = new Signature(OCULUS_TV_PROD_CERT);
    public static final Signature OSMETA_DEBUG_SIG = new Signature(OSMETA_SIGNATURE_HASH_DEBUG);
    public static final String OSMETA_SIGNATURE_HASH_DEBUG = "";
    public static final Signature QUEST_PLATFORM_RELEASE_SIGNATURE = new Signature(QUEST_PLATFORM_SIGNATURE_HASH_RELEASE);
    public static final String QUEST_PLATFORM_SIGNATURE_HASH_RELEASE = "";
    public static final Signature STANDALONE_PLATFORM_DEBUG_SIGNATURE = new Signature(STANDALONE_PLATFORM_SIGNATURE_HASH_DEBUG);
    public static final String STANDALONE_PLATFORM_SIGNATURE_HASH_DEBUG = "";
    private static final String TAG = "SignatureHelper";

    public static boolean verifyDebugFirstPartyApplication(Context context, String str, boolean z) {
        if (z) {
            return verifyDebugFirstPartyApplication(context, str);
        }
        Log.e(TAG, "Not a trusted user. Do you have Unlockulus or pass the IsTrustedUser gk?");
        return false;
    }

    public static boolean verifyDebugFirstPartyApplication(Context context, String str) {
        return verifyApplication(context, str, FBANDROID_FIRST_PARTY_DEBUG_SIG) || verifyApplication(context, str, OCULUS_FIRST_PARTY_DEBUG_SIG);
    }

    public static boolean verifyProductionFirstPartyApplication(Context context, String str) {
        return verifyApplication(context, str, OCULUS_FIRST_PARTY_PROD_SIG) || verifyApplication(context, str, OCULUS_PROD_RELEASE_SIGNATURE) || verifyApplication(context, str, OCULUS_CORE_RELEASE_SIGNATURE) || verifyApplication(context, str, OCULUS_TV_PROD_SIG);
    }

    public static boolean verifyMatchesSelfSignature(Context context, String str) {
        try {
            for (Signature signature : context.getPackageManager().getPackageInfo(context.getPackageName(), 64).signatures) {
                if (verifyApplication(context, str, signature)) {
                    return true;
                }
            }
        } catch (PackageManager.NameNotFoundException e) {
            Log.e(TAG, "Failed to get signatures of own package?", e);
        }
        return false;
    }

    private static boolean verifyApplication(Context context, String str, Signature signature) {
        Signature signature2 = null;
        try {
            Signature[] signatureArr = context.getPackageManager().getPackageInfo(str, 64).signatures;
            if (signatureArr.length > 0) {
                signature2 = signatureArr[0];
            }
        } catch (PackageManager.NameNotFoundException unused) {
        }
        if (signature.equals(signature2)) {
            return true;
        }
        return false;
    }
}
